package com.example.examen_ciudades_javafx;

import javafx.beans.value.ChangeListener;
import javafx.beans.value.ObservableValue;
import javafx.collections.FXCollections;
import javafx.collections.ObservableList;
import javafx.event.ActionEvent;
import javafx.fxml.FXML;
import javafx.fxml.Initializable;
import javafx.scene.control.Button;
import javafx.scene.control.Label;
import javafx.scene.control.ListView;
import javafx.scene.image.Image;
import javafx.scene.image.ImageView;
import javafx.scene.layout.Pane;

import java.net.URL;
import java.util.ArrayList;
import java.util.ResourceBundle;

public class HelloController implements Initializable {
    @FXML
    private Button btn_limpiarEstilo;

    @FXML
    private Button btn_Viajar;

    @FXML
    private Button btn_asignarEstilo;

    @FXML
    private Label welcomeText;

    @FXML
    private ListView listview;

    @FXML
    private ImageView image;

    //TODO Debe ser un lista Observable
    private ArrayList<Ciudad> lista_ciudades;
    @FXML
    private Pane pane;

    @FXML
    void btnViajarOnClick(ActionEvent event) {

    }

    @FXML
    void limpiarEstiloOnClick(ActionEvent event) {
        this.pane.getStyleClass().remove("mi_clase_estilo1");

    }

    @FXML
    void asignarEstiloOnClick(ActionEvent event) {
        this.pane.getStyleClass().add("mi_clase_estilo1");

    }

    @Override
    public void initialize(URL location, ResourceBundle resources) {
        Ciudad ciudad_sevilla = new Ciudad("Sevilla", "https://media.istockphoto.com/id/576913134/es/foto/plaza-de-espa%C3%B1a-sevilla-espa%C3%B1a.jpg?s=612x612&w=0&k=20&c=rStguWgG9JACG9yTDWOUQ78mF-w2MdmRB393JOKR328=", 50.0);

        Ciudad ciudad_barcelona = new Ciudad("Barcelona", "https://media.istockphoto.com/id/1320014700/es/foto/catedral-de-la-sagrada-familia-en-primavera-barcelona-espa%C3%B1a.jpg?s=612x612&w=0&k=20&c=QPWMEyWrQeYpf2kzdDXpfYbVSE9fvYOUNWImtXFshcg=", 80.0);

        Ciudad ciudad_malaga = new Ciudad("Malaga", "https://upload.wikimedia.org/wikipedia/commons/4/40/M%C3%A1laga_-_vistas_desde_el_Parador_de_Gibralfaro_01_%28cropped%29.jpg", 40.0);

        Ciudad ciudad_madrid = new Ciudad("Madrid", "data:image/jpeg;base64,", 60.0);

        lista_ciudades = new ArrayList<Ciudad>();
        lista_ciudades.add(ciudad_barcelona);
        lista_ciudades.add(ciudad_madrid);
        lista_ciudades.add(ciudad_malaga);
        lista_ciudades.add(ciudad_sevilla);

        for (Ciudad c : lista_ciudades) {
            listview.getItems().add(c);
        }
        System.out.println("ciudades "+lista_ciudades.size());

        listview.getSelectionModel().selectedItemProperty().addListener(new ChangeListener() {
            @Override
            public void changed(ObservableValue observable, Object oldValue, Object newValue) {
                image = new ImageView(new Image(((Ciudad) newValue).getUrl()));

                pane.getChildren().set(0,image);

            }
        });
    }
}